from typing import *
import collections
import functools
import itertools
import math
import sys

INF = float('inf')


def solve(N: int, P: "List[int]"):
    ans = 0
    l = INF
    for pi in P:
        l = min(l, pi)
        if pi <= l:
            ans += 1
    return ans


def main():

    sys.setrecursionlimit(10 ** 6)

    def iterate_tokens():
        for line in sys.stdin:
            for word in line.split():
                yield word
    tokens = iterate_tokens()
    N = int(next(tokens))  # type: int
    P = [int(next(tokens)) for _ in range(N)]  # type: "List[int]"
    print(f'{solve(N, P)}')


if __name__ == '__main__':
    main()
